// Advanced Parallel Processing with Intelligent Data Distribution
// Implements sophisticated data transformation for multi-agent coordination

const data = $input.item.json;
const orchestratorResponse = data.text || data.response || JSON.stringify(data);
const originalRequest = $input.all()[0].json;

// Advanced data extraction with fallback logic
const extractTravelData = () => {
  const travelInfo = {
    destination: originalRequest.destination || data.destination || '',
    startDate: originalRequest.startDate || data.startDate || '',
    endDate: originalRequest.endDate || data.endDate || '',
    budget: parseFloat(originalRequest.budget || data.budget || 0),
    travelers: parseInt(originalRequest.travelers || data.travelers || 1),
    preferences: originalRequest.preferences || data.preferences || {},
    sessionId: originalRequest.sessionId || Date.now().toString(),
    timestamp: new Date().toISOString()
  };

  // Calculate derived metrics
  travelInfo.duration = calculateDuration(travelInfo.startDate, travelInfo.endDate);
  travelInfo.budgetPerPerson = travelInfo.budget / travelInfo.travelers;
  travelInfo.dailyBudget = travelInfo.budget / travelInfo.duration;

  return travelInfo;
};

const calculateDuration = (start, end) => {
  if (!start || !end) return 7; // Default duration
  const startDate = new Date(start);
  const endDate = new Date(end);
  return Math.ceil((endDate - startDate) / (1000 * 60 * 60 * 24)) + 1;
};

const travelInfo = extractTravelData();

// Create specialized agent payloads with enhanced context
const agentPayloads = [
  {
    json: {
      ...originalRequest,
      ...travelInfo,
      orchestratorPlan: orchestratorResponse,
      agentType: 'weather_intelligence',
      agentRole: 'Climate and Environmental Analysis',
      agentTask: 'Analyze weather patterns, seasonal factors, and environmental conditions to optimize travel planning',
      priority: 1,
      requiredOutputs: ['temperature_range', 'precipitation_probability', 'seasonal_events', 'packing_recommendations'],
      reasoning: 'Weather analysis must complete first as it impacts all other planning decisions'
    }
  },
  {
    json: {
      ...originalRequest,
      ...travelInfo,
      orchestratorPlan: orchestratorResponse,
      agentType: 'flight_optimization',
      agentRole: 'Aviation and Transportation Specialist',
      agentTask: 'Search and optimize flight options across multiple carriers, considering price, convenience, and timing factors',
      priority: 2,
      requiredOutputs: ['flight_options', 'price_comparison', 'booking_recommendations', 'alternative_routes'],
      budgetAllocation: travelInfo.budgetPerPerson * 0.4, // Allocate 40% for flights
      reasoning: 'Flight costs significantly impact remaining budget for accommodations and activities'
    }
  },
  {
    json: {
      ...originalRequest,
      ...travelInfo,
      orchestratorPlan: orchestratorResponse,
      agentType: 'experience_curation',
      agentRole: 'Local Experience and Activity Curator',
      agentTask: 'Discover and curate authentic experiences, attractions, dining, and activities matched to traveler preferences',
      priority: 3,
      requiredOutputs: ['must_see_attractions', 'dining_recommendations', 'unique_experiences', 'hidden_gems'],
      budgetAllocation: travelInfo.budgetPerPerson * 0.3, // Allocate 30% for activities
      reasoning: 'Experience curation benefits from weather and flight data to optimize recommendations'
    }
  }
];

// Add metadata for tracking and debugging
agentPayloads.forEach((payload, index) => {
  payload.json.metadata = {
    workflowId: $execution.id,
    agentIndex: index,
    totalAgents: agentPayloads.length,
    processingTimestamp: new Date().toISOString()
  };
});

return agentPayloads;
